import React from "react";

const OriginalList = (props) => {
  const [movieOriginal, setMovieOriginal] = useState({});

  const fetchOriginal = async () => {
    try {
      const response = await fetch(props.originalUrl);
      if (!response.ok) {
        throw new Error("Fetch trending to failed");
      }
      const data = await response.json();

      const trendingMovie =
        data.results[Math.floor(Math.random() * data.results.length - 1)];

      setMovieTrending(trendingMovie);
    } catch (error) {
      alert(error.message);
    }
  };

  useEffect(() => {
    fetchTrending();
  }, []);
  return;
};

export default OriginalList;
