import React, { useEffect, useState } from "react";
import NavBar from "./NavBar";

const API_KEY = "b10f26ad1365b480abc56ae5b5d98080";
function Browse() {
  const [movie, setMovie] = useState({});

  const requests = {
    fetchTrending: `https://api.themoviedb.org/3/trending/all/week?api_key=${API_KEY}&language=en-US`,
    fetchNetflixOriginals: `https://api.themoviedb.org/3/discover/tv?api_key=${API_KEY}&with_network=123S`,
    fetchTopRated: `https://api.themoviedb.org/3/movie/top_rated?api_key=${API_KEY}&language=en-US`,
    fetchActionMovies: `https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&with_genres=28`,
    fetchComedyMovies: `https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&with_genres=35`,
    fetchHorrorMovies: `https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&with_genres=27`,
    fetchRomanceMovies: `https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&with_genres=10749`,
    fetchDocumentaries: `https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&with_genres=99`,
    fetchSearch: `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=en-US`,
  };

  console.log(movie);
  const title = movie.title ? movie.title : movie.name;

  return (
    <div>
      <NavBar
        title={title}
        overview={movie.overview}
        banner={movie.backdrop_path}
        trendingUrl={requests.fetchTrending}
      />
    </div>
  );
}

export default Browse;
