import React, { useState, useEffect } from "react";
import "./NavBar.css";
import { Link } from "react-router-dom";
import useFetch from "../../hooks/use-fetch";

const NavBar = (props) => {
  const [movieTrending, setMovieTrending] = useState([]);

  useFetch(
    "https://api.themoviedb.org/3/discover/tv?api_key=b10f26ad1365b480abc56ae5b5d98080&with_network=123S",
    randomMovie
  );
  const fetchTrending = async () => {
    try {
      const response = await fetch(props.trendingUrl);
      if (!response.ok) {
        throw new Error("Fetch trending to failed");
      }
      const data = await response.json();

      const trendingMovie =
        data.results[Math.floor(Math.random() * data.results.length - 1)];

      setMovieTrending(trendingMovie);
    } catch (error) {
      alert(error.message);
    }
  };

  useEffect(() => {
    fetchTrending();
  }, []);

  const title = movieTrending.title ? movieTrending.title : movieTrending.name;

  return (
    <div className="navbar">
      <img
        src={`https://image.tmdb.org/t/p/w500/${movieTrending.backdrop_path}`}
        alt={title}
        width="100%"
      />

      <Link to="/">
        <h2>Movie App</h2>
      </Link>
      <Link to="/search">
        <svg
          className="svg-inline--fa fa-search fa-w-16"
          fill="#ccc"
          aria-hidden="true"
          data-prefix="fas"
          data-icon="search"
          role="img"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 512 512"
        >
          <path d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z"></path>
        </svg>
      </Link>
      <div>
        <h1>{title}</h1>
        <button>Play</button>
        <button>Add List</button>
        <p>{movieTrending.overview}</p>
      </div>
    </div>
  );
};

export default NavBar;
