import React, { useState } from "react";
import "./MovieList.css";
import useFetch from "../../../hooks/use-fetch";
// import OriginalList from "./OriginalList";
// import TrendingList from "./TrendingList";
// import TopList from "./TopList";
// import ActionList from "./ActionList";
// import ComedyList from "./ComedyList";
// import HorrorList from "./HorrorList";
// import RomanceList from "./RomanceList";
// import DocumentList from "./DocumentList";

const MovieList = (props) => {
  const [movieList, setMovieList] = useState([]);

  useFetch(props.url, dataHandler);

  function dataHandler(e) {
    setMovieList(e.results);
  }

  let isOriginal = props.type === "Original";

  const movieListArr = movieList.map((item) => {
    return (
      <img
        key={item.id}
        src={`https://image.tmdb.org/t/p/original/${
          isOriginal ? item.poster_path : item.backdrop_path
        }`}
        alt={item.title}
        width="100%"
      />
    );
  });
  return (
    <div className="main-list">
      <h2>{props.type}</h2>
      <div>{movieListArr}</div>
      {/* <OriginalList originalUrl={props.originalUrl} />
      <TrendingList trendingUrl={props.trendingUrl} />
      <TopList topUrl={props.topUrl} />
      <ActionList actionUrl={props.actionUrl} />
      <ComedyList comedyUrl={props.comedyUrl} />
      <HorrorList horrorUrl={props.horrorUrl} />
      <RomanceList romanceUrl={props.romanceUrl} />
      <DocumentList documentUrl={props.documentUrl} /> */}
    </div>
  );
};

export default MovieList;
