import React, { useEffect } from "react";

const useFetch = (url, handle) => {
  const fetchMovie = async () => {
    try {
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error("Fetch to failed");
      }

      const data = response.json();
      console.log(data);

      handle(data);
    } catch (error) {
      alert(error.message);
    }
  };

  useEffect(() => {
    fetchMovie();
  }, []);

  return { fetchMovie };
};

export default useFetch;
