import React, { useState, useEffect } from "react";

const TopList = (props) => {
  const [topMovie, setTopMovie] = useState([]);

  const fetchTop = async () => {
    try {
      const response = await fetch(props.topUrl);

      if (!response.ok) {
        throw new Error("Fetch Top movie to failed");
      }

      const data = await response.json();
      setTopMovie(data.results);
    } catch (error) {
      alert(error.message);
    }
  };

  useEffect(() => {
    fetchTop();
  }, []);

  return (
    <div>
      <h2>Xu hướng</h2>
      <div className="movie-list">
        {topMovie.map((movie) => {
          return (
            <img
              src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`}
              alt={movie.name}
              width="100%"
              key={movie.id}
            />
          );
        })}
      </div>
    </div>
  );
};

export default TopList;
