import React, { useState, useEffect } from "react";

const OriginalList = (props) => {
  const [movieOriginal, setMovieOriginal] = useState({});

  const fetchOriginal = async () => {
    try {
      const response = await fetch(props.originalUrl);
      if (!response.ok) {
        throw new Error("Fetch Original to failed");
      }
      const data = await response.json();

      //   const trendingMovie =
      //     data.results[Math.floor(Math.random() * data.results.length - 1)];

      setMovieOriginal(data.results);
    } catch (error) {
      alert(error.message);
    }
  };

  useEffect(() => {
    fetchOriginal();
  }, []);

  console.log(movieOriginal);
  return (
    <div>
      {movieOriginal.map((movie) => {
        <img
          src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`}
          // alt={title}
          width="100%"
        />;
      })}
    </div>
  );
};

export default OriginalList;
