import React, { useState, useEffect } from "react";

const RomanceList = (props) => {
  const [romanceMovie, setRomanceMovie] = useState([]);

  const fetchRomance = async () => {
    try {
      const response = await fetch(props.romanceUrl);

      if (!response.ok) {
        throw new Error("Fetch Romance to failed");
      }

      const data = await response.json();
      setRomanceMovie(data.results);
    } catch (error) {
      alert(error.message);
    }
  };

  useEffect(() => {
    fetchRomance();
  }, []);

  return (
    <div>
      <h2>Lãng mạn</h2>
      <div className="movie-list">
        {romanceMovie.map((movie) => {
          return (
            <img
              src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`}
              alt={movie.name}
              width="100%"
              key={movie.id}
            />
          );
        })}
      </div>
    </div>
  );
};

export default RomanceList;
