import React, { useState, useEffect } from "react";
import MovieDetail from "./MovieDetail";

const OriginalList = (props) => {
  const [movieOriginal, setMovieOriginal] = useState([]);

  const fetchOriginal = async () => {
    try {
      const response = await fetch(props.originalUrl);
      if (!response.ok) {
        throw new Error("Fetch Original to failed");
      }
      const data = await response.json();

      setMovieOriginal(data.results);
    } catch (error) {
      alert(error.message);
    }
  };

  useEffect(() => {
    fetchOriginal();
  }, []);

  const detailMovie = (e) => {
    console.log(e);
    const a = movieOriginal.filter((id) => (id = e.id));
    console.log(a);
  };

  return (
    <div className="movie-list">
      {movieOriginal.map((movie) => {
        return (
          <img
            src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
            alt={movie.name}
            width="100%"
            key={movie.id}
            onClick={detailMovie}
          />
        );
      })}
      <MovieDetail />
    </div>
  );
};

export default OriginalList;
